//6.Write a program to swap two number.

package Logical_program;
public class Swap_6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			int x=10,y=20;
			int z;
			z=x;
			x=y;
			y=z;
			System.out.println("x = "+x);
			System.out.println("y = "+y);
			
			
			
			
	}

}
